$(function(){$('a[href^=#]').click(function(){var speed=400;var href=$(this).attr("href");var target=$(href=="#"||href==""?'html':href);var position=target.offset().top;$('body,html').animate({scrollTop:position},speed,'swing');return false;});});$(function(){$('a img').hover(function(){$(this).attr('src',$(this).attr('src').replace('_off','_on'));},function(){if(!$(this).hasClass('currentPage')){$(this).attr('src',$(this).attr('src').replace('_on','_off'));}});});$(function(){var showFlug=false;var topBtn=$('#pagetop');topBtn.css('bottom','-100px');var showFlug=false;$(window).scroll(function(){if($(this).scrollTop()>100){if(showFlug==false){showFlug=true;topBtn.stop().animate({'bottom':'20px'},200);}}else{if(showFlug){showFlug=false;topBtn.stop().animate({'bottom':'-100px'},200);}}});topBtn.click(function(){$('body,html').animate({scrollTop:0},500);return false;});});$(document).ready(function(){$('#target').bind('change',function(){var linkurl=$('#target').val();$('#link').attr({href:linkurl});});});